import { AnyPointerEvent } from 'framer-motion/types/gestures/PanSession'
import { Key, useEffect } from 'react'
import { toast } from 'react-toastify'
import { useAppDispatch, useAppSelector } from '../app/hooks'
import Card from '../components/Card'
import Spinner from '../components/Spinner'
import { DeckInterface } from '../config/interface'
import { getBlog } from '../features/blogs/blogSlice'

// const colors = ['red', 'black', 'purple']

const Deck = () => {
  const { blogs, isError, isLoading, message } = useAppSelector((state) => state.blogs)
  const dispatch = useAppDispatch()

  useEffect(() => {
    if(isError) {
      toast.error(message)
    }

    dispatch(getBlog())
  }, [dispatch, isError, message])

  isLoading && <Spinner />


  return (
    <div className="h-[40vh] w-full items-center flex justify-center">
      <div className='text-center flex flex-col items-center'>
        <p className='text-sm font-light'>Swipe right to add to your library, left to discard</p>
        <div className='relative w-60'>
          {
            blogs.map((val: AnyPointerEvent, index: Key | null | undefined) => (
              <Card 
                key={index}
                id={val.id}
                title={val.title}
                body={val.body}
                description={val.description}
                user={val.user}
              />
            ))
          }
        </div>
      </div>
    </div>
  )
}

export default Deck
