import { useEffect } from 'react'
import { toast } from 'react-toastify'
import { useAppDispatch, useAppSelector } from '../app/hooks'
// import Card from '../components/Card'
import Spinner from '../components/Spinner'
import { getBlog } from '../features/blogs/blogSlice'

// const colors = ['red', 'black', 'purple']

const Deck = () => {
  const { blogs, isError, isLoading, message } = useAppSelector((state) => state.blogs)
  const dispatch = useAppDispatch()

  useEffect(() => {
    if(isError) {
      toast.error(message)
    }

    dispatch(getBlog())
  }, [dispatch, isError, message])

  isLoading && <Spinner />

  console.log(blogs)

  return (
    <div className="h-[40vh] w-full items-center flex justify-center">
      <div className='text-center flex flex-col items-center'>
        <p className='text-sm font-light'>Swipe right to add to your library, left to discard</p>
        <div className='relative w-60'>
          {}
        </div>
      </div>
    </div>
  )
}

export default Deck
