import React, { useEffect, useState} from 'react'
import SignInIcon from '@mui/icons-material/Login'
import PersonIcon from '@mui/icons-material/Person'
import LockIcon from '@mui/icons-material/Lock'
import { Link, useNavigate } from 'react-router-dom'
import { useAppDispatch, useAppSelector } from '../app/hooks'
import Spinner from '../components/Spinner'
import { toast } from 'react-toastify'
import { register, reset } from '../features/auth/authSlice'

const Register = () => {
  const navigate = useNavigate()
  const dispatch = useAppDispatch()
  const [formData, setFormData] = useState({userName: '', email: '', password: ''})
  const  {userName, email, password } = formData;
  const { user, isLoading, isSuccess, isError, message } = useAppSelector((state) => state.auth)

  useEffect(() => {
    if(isError) {
      toast.error(message)
    }

    // if(isSuccess || user) {
    //   navigate('/')
    // }
    dispatch(reset())
  }, [dispatch, isError, isSuccess, message, navigate, user])

  const onChange = (e: { target: { name: string, value: string } }) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value
    }))
  }

  const onSubmit = (e: { preventDefault: () => void }) => {
    e.preventDefault();

    const userData = {
      userName,
      email,
      password
    }

    dispatch(register(userData))
  }

  isLoading && <Spinner />

  return (
    <div className="h-[80vh] w-full items-center flex justify-center">
      <div className='space-y-2'>
        <form className='grid gap-5 text-center' onSubmit={onSubmit}>
          <div className='flex justify-center'>
            <SignInIcon sx={{fontSize: 70, color: 'white'}} />
          </div>
          <div className='grid gap-2'>
          <div className='bg-white bg-opacity-20 rounded-2xl p-2 flex space-x-1 text-white'>
              <PersonIcon sx={{color: 'white'}} />
              <input 
                type='text' 
                className='bg-transparent w-full outline-none'
                placeholder='Enter UserName' 
                name='userName'
                value={userName}
                onChange={onChange}
              />
            </div>
            <div className='bg-white bg-opacity-20 rounded-2xl p-2 flex space-x-1 text-white'>
              <PersonIcon sx={{color: 'white'}} />
              <input 
                type='email' 
                className='bg-transparent w-full outline-none'
                placeholder='Enter Email' 
                name='email'
                value={email}
                onChange={onChange}
              />
            </div>
            <div className='bg-white bg-opacity-20 rounded-2xl p-2 flex space-x-1 text-white'>
              <LockIcon sx={{color: 'white'}} />
              <input 
                type='password' 
                className='bg-transparent w-full outline-none'
                placeholder='Enter Password' 
                name='password'
                value={password}
                onChange={onChange}
              />
            </div>
          </div>
          <div className='bg-[#b6fff4] p-2 rounded-2xl cursor-pointer'>
            <button className='uppercase text-black' type='submit'>Register</button>
          </div>
        </form>
        <div className='text-xs text-white flex justify-end'>
          <p className='cursor-pointer hover:text-[#b6fff4]'>Forgot Password?</p>
        </div>
        <div className='text-xs text-white flex justify-between'>
          <Link to='register'>
            <p className='cursor-pointer hover:text-[#b6fff4]'>Already have an Account?</p>
          </Link>
          <p className='cursor-pointer hover:text-[#b6fff4]'>Need Help?</p>
        </div>
      </div>
    </div>
  )
}

export default Register
