import React from 'react'
import { Avatar, Box, Button, IconButton } from '@mui/material'
import { Link, useNavigate } from 'react-router-dom';
import { useAppDispatch } from '../app/hooks';
import { logout, reset } from '../features/auth/authSlice';
import Popper from '@mui/material/Popper'

const Header = () => {
  let user = JSON.parse(localStorage.getItem('user') || 'null')
  const dispatch = useAppDispatch()
  const navigate = useNavigate()
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(anchorEl ? null : event.currentTarget);
  };

  const open = Boolean(anchorEl);
  const id = open ? 'simple-popper' : undefined;

  const onLogout = () => {
    dispatch(logout())
    dispatch(reset())
    navigate('/')
  }

  const addBlog = () => {
    navigate('add')
  }

  return (
    <div className='bg-transparent flex p-5 justify-between items-center'>
      <div>
        <Link to='/'>
          <p className='text-xl sm:text-2xl underline font-serif text-[#b6fff4] font-bold uppercase'>Saadatu_blog</p>
        </Link>
      </div>
      {!user ? (
        <div className='space-x-3'>
          <Link to='login'>
            <Button sx={{fontSize: 13, padding: 0.3, color: '#b6fff4'}}>Login</Button>
          </Link>
          <Link to='register'>
            <Button sx={{fontSize: 13, padding: 0.3, color: '#b6fff4'}}>Register</Button>
          </Link>
        </div>
      ): (
        <div className='flex items-center'>
          <>
            <IconButton aria-describedby={id} onClick={handleClick}>
              <Avatar sx={{width: 28, height: 28, backgroundColor: '#b6fff4'}}>
                {user.name.charAt(0)}
              </Avatar>
            </IconButton>
            <Popper id={id} open={open} anchorEl={anchorEl}>
              <Box sx={{ border: 1, p: 1, bgcolor: 'background.paper' }} className='divide-y-2'>
                <div className='p-2 cursor-pointer hover:bg-slate-300' >
                  <p>Add a blog</p>
                </div>
                <div className='p-2 cursor-pointer hover:bg-slate-300'>
                  <p>Add a blog</p>
                </div>
              </Box>
            </Popper>
          </>
          <Button sx={{color: '#b6fff4', }} onClick={onLogout} >Logout</Button>
        </div>
      )}
    </div>
  )
}

export default Header;
