import React from 'react'
import { motion, useMotionValue, useTransform, useAnimation } from 'framer-motion'
import { DeckInterface } from '../config/interface';
import image from '../imgs/download.jpeg'

const Card: React.FC<DeckInterface> = ({_id, body, description, title, user}) => {
  const motionValue: any = useMotionValue(0);
  const rotateValue: any = useTransform(motionValue, [-200, 200], [-50, 50])
  const opacityValue: any = useTransform(motionValue, [-200, -150, 0, 150, 200], [0, 1, 1, 1, 0]);
  const animControls = useAnimation()

  const addToLibrary = () => {
    console.log('Library')
  }


  return (
    <motion.div
      drag='x'
      style={{x: motionValue ,rotate: rotateValue, opacity: opacityValue}}
      dragConstraints={{ left: -1000, right: 1000}}
      onDragEnd={(event, info) => {
        if(Math.abs(info.point.x) <= 300) {
          animControls.start({x: 100});
        } else {
          animControls.start({x: info.point.x < 0 ? -200: 200})
        }
        if(info.point.x > 200) {
          addToLibrary()
        }
      }}
      className='absolute top-8 left-20 cursor-pointer'
    >
      <div className='bg-slate-300 rounded-lg shadow-slate-800 shadow-md'>
        <div className='rounded-b-lg'>
          <img alt='' src={image} />
        </div>
        <div className=' rounded-t-lg '>
          <h1>{title}</h1>
        </div>
      </div>
    </motion.div>
  )
}

export default Card
