import React from 'react'
import { motion, useMotionValue, useTransform, useAnimation } from 'framer-motion'
import Card from '../components/Card'

interface DeckInterface {
  img: string,
  title: string
}

const Deck: React.FC<DeckInterface> = ({img, title}: DeckInterface) => {
  const motionValue: any = useMotionValue(0);
  const rotateValue: any = useTransform(motionValue, [-200, 200], [-50, 50])
  const opacityValue: any = useTransform(motionValue, [-200, -150, 0, 150, 200], [0, 1, 1, 1, 0]);
  const animControls = useAnimation()

  return (
    <motion.div
      drag='x'
      animate={{x: motionValue ,rotate: rotateValue, opacity: opacityValue}}
      dragConstraints={{ left: -50, right: 50}}
      onDragEnd={(event, info) => {
        if(Math.abs(info.point.x) <= 150) {
          animControls.start({x: 0});
        } else {
          animControls.start({x: info.point.x < 0 ? -200: 200})
        }
      }}
      style={{}}
    >
      <Card />
    </motion.div>
  )
}

export default Deck
