import Header from "./components/Header";
import Home from "./pages/Home";
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'
import Blog from "./pages/Blog";
import Deck from "./pages/Deck";

function App() {
  return (
    <Router>
      <div className="bg-bg-img overflow-hidden h-screen w-screen bg-cover md:bg-contain">
        <div className="bg-black h-full bg-opacity-30">
          <Header />
          <div className="h-[70vh] w-full items-center flex justify-center">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path='blogs/:id' element={<Blog />} />
              <Route path='blogs' element={<Deck />} />
              <Route path="*" element={<Navigate to='/' replace />} />
            </Routes>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
